import React, { useState } from 'react';

import { BookOpen, Brain, Calendar, HelpCircle, Loader2 } from 'lucide-react';

function App() {
  const [topic, setTopic] = useState('');
  const [days, setDays] = useState('10');
  const [hours, setHours] = useState('1.5');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!topic) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ topic, days: parseInt(days), hours_per_day: parseFloat(hours) })
      });
      const data = await response.json();
      if (data.error) {
        alert(`Error: ${data.error}`);
      } else {
        setResult(data);
      }
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl font-extrabold bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent mb-4">
            AI Study Assistant
          </h1>
          <p className="text-xl text-gray-300">Transform any topic into comprehensive study materials</p>
        </div>

        {/* Input Form */}
        <div className="bg-gray-800 rounded-2xl p-8 mb-8 border border-gray-700 shadow-2xl">
          <div className="flex items-center gap-3 mb-6">
            <Brain className="h-8 w-8 text-purple-400" />
            <h2 className="text-2xl font-bold text-white">Create Your Study Plan</h2>
          </div>

          <form onSubmit={handleSubmit} className="space-y-8">
            <div className="space-y-3">
              <label className="text-lg font-semibold text-gray-200">Study Topic</label>
              <input
                type="text"
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
                placeholder="e.g., Machine Learning Fundamentals"
                className="w-full p-4 bg-gray-700 border border-gray-600 rounded-xl text-white placeholder-gray-400 focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-3">
                <label className="text-lg font-semibold text-gray-200">Study Days</label>
                <input
                  type="number"
                  value={days}
                  onChange={(e) => setDays(e.target.value)}
                  min="1"
                  max="30"
                  className="w-full p-4 bg-gray-700 border border-gray-600 rounded-xl text-white focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                />
              </div>

              <div className="space-y-3">
                <label className="text-lg font-semibold text-gray-200">Hours per Day</label>
                <input
                  type="number"
                  step="0.5"
                  value={hours}
                  onChange={(e) => setHours(e.target.value)}
                  min="0.5"
                  max="8"
                  className="w-full p-4 bg-gray-700 border border-gray-600 rounded-xl text-white focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                />
              </div>
            </div>

            <button 
              type="submit" 
              disabled={loading || !topic} 
              className="w-full py-4 px-8 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 disabled:from-gray-600 disabled:to-gray-600 rounded-xl font-bold text-lg transition-all transform hover:scale-105 disabled:scale-100 disabled:cursor-not-allowed"
            >
              {loading ? (
                <div className="flex items-center justify-center gap-3">
                  <Loader2 className="h-6 w-6 animate-spin" />
                  Generating Amazing Content...
                </div>
              ) : (
                'Generate Study Material ‚ú®'
              )}
            </button>
          </form>
        </div>

        {/* Results */}
        {result && (
          <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
            <div className="bg-gradient-to-br from-blue-900 to-blue-800 rounded-xl p-4 border border-blue-700 shadow-lg">
              <div className="flex items-center gap-2 mb-3">
                <BookOpen className="h-5 w-5 text-blue-300" />
                <h3 className="text-lg font-bold text-white">üìö Summary</h3>
              </div>
              <div className="whitespace-pre-wrap text-sm text-gray-100 leading-relaxed bg-blue-800/30 p-3 rounded-lg max-h-64 overflow-y-auto">
                {result.summary}
              </div>
            </div>

            <div className="bg-gradient-to-br from-green-900 to-green-800 rounded-xl p-4 border border-green-700 shadow-lg">
              <div className="flex items-center gap-2 mb-3">
                <Brain className="h-5 w-5 text-green-300" />
                <h3 className="text-lg font-bold text-white">üß† Study Notes</h3>
              </div>
              <div className="whitespace-pre-wrap text-sm text-gray-100 leading-relaxed bg-green-800/30 p-3 rounded-lg max-h-64 overflow-y-auto">
                {result.notes}
              </div>
            </div>

            <div className="bg-gradient-to-br from-purple-900 to-purple-800 rounded-xl p-4 border border-purple-700 shadow-lg">
              <div className="flex items-center gap-2 mb-3">
                <Calendar className="h-5 w-5 text-purple-300" />
                <h3 className="text-lg font-bold text-white">üìÖ Study Plan</h3>
              </div>
              <div className="whitespace-pre-wrap text-sm text-gray-100 leading-relaxed bg-purple-800/30 p-3 rounded-lg max-h-64 overflow-y-auto">
                {result.plan}
              </div>
            </div>

            <div className="bg-gradient-to-br from-orange-900 to-orange-800 rounded-xl p-4 border border-orange-700 shadow-lg">
              <div className="flex items-center gap-2 mb-3">
                <HelpCircle className="h-5 w-5 text-orange-300" />
                <h3 className="text-lg font-bold text-white">‚ùì Quiz</h3>
              </div>
              <div className="whitespace-pre-wrap text-sm text-gray-100 leading-relaxed bg-orange-800/30 p-3 rounded-lg max-h-64 overflow-y-auto">
                {result.quiz}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
